import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CheckerService } from 'src/app/Services/checker.service';
import { RestApiServiceService } from 'src/app/Services/rest-api-service.service';
import { SesionService } from 'src/app/Services/sesion-service.service';
import { SwalService } from 'src/app/Services/swal-service.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-league',
  templateUrl: './league.component.html',
  styleUrls: ['./league.component.css'],
})
export class LeagueComponent implements OnInit {
  constructor(
    private checker: CheckerService,
    private swal: SwalService,
    private sesion: SesionService,
    private restApi: RestApiServiceService,
    private router: Router
  ) {}
  league: any = null;
  myTeams = ['asd', 'abdddd'];
  ngOnInit(): void {
    this.sesion.refreshUser();

    this.getLeague();
  }
  getLeague() {
    const leagueName: string = this.sesion.getUser().privateLeague;
    if (leagueName === '' || leagueName.length == 0) {
      this.league = null;
      return;
    }
    console.log(leagueName);

    this.restApi
      .get_request('PrivateLeague/' + leagueName, null)
      .subscribe((result) => {
        console.log(result);

        this.league = result;
      });
  }
  createNewLeague(): void {
    Swal.fire({
      title: 'Inserte el nombre de la liga',
      input: 'text',
      showCancelButton: true,
      confirmButtonText: 'Crear',
    }).then((result) => {
      if (!result.isConfirmed) return;
      if (!this.checker.verifyName(result.value, 5, 30, true)) {
        this.swal.showError(
          'Nombre inválido',
          'El nombre de la liga debe ser un valor alfa numérico no vacío'
        );
        return;
      }

      const data = {
        leagueCreatorUsername: this.sesion.getUser().username,
        name: result.value,
      };
      this.restApi.post_request('PrivateLeague', data).subscribe(
        (response) => {
          this.swal.showSuccess(
            'Liga Creada',
            'Tu liga se ha creado exitosamente'
          );
          document.location.reload();
        },
        (error) => {
          this.swal.showError(
            'Error al crear Liga',
            'Ha ocurrido un error al crear esta liga'
          );
        }
      );
    });
  }

  joinToLeague(): void {
    Swal.fire({
      title: 'Inserte el código de acceso',
      input: 'text',
      showCancelButton: true,
      confirmButtonText: 'Crear',
    }).then((result) => {
      if (!result.isConfirmed || result.value < 4 || result.value === '') {
        this.swal.showError(
          'Código no válido',
          'Solicite un código de acceso válido'
        );
        return;
      }
      const data = {
        invitationCode: result.value,
        playerUsername: this.sesion.getUser().username,
      };
      this.restApi.post_request('PrivateLeague/joinRequest', data).subscribe(
        (result) => {
          this.swal.showSuccess(
            'Solicitud envidad',
            'Un administrador de esta liga verificará tu solicitud'
          );
        },
        (error) => {
          this.swal.showError(
            'Código no válido',
            'El código ingresado no pertenece a ninguna liga privada'
          );
        }
      );
    });
  }
  teamIsOfUser(teamName: string): boolean {
    return this.myTeams.includes(teamName);
  }

  openProfile(userName: string): void {
    this.router.navigate(['pages/profile'], {
      queryParams: { userName: userName },
    });
  }
}

import { Injectable } from '@angular/core';
import Swal, { SweetAlertInput } from 'sweetalert2';
@Injectable({
  providedIn: 'root',
})
export class SwalService {
  showError(title: string, text: string) {
    Swal.fire({
      icon: 'error',
      title: title,
      text: text,
    });
  }

  showErrorHTML(title: string, text: string) {
    Swal.fire({
      icon: 'error',
      title: title,
      html: text,
    });
  }

  showSuccess(title: string, text: string) {
    Swal.fire({
      icon: 'success',
      title: title,
      text: text,
    });
  }

  optionSwal(
    title: string,
    text: string,
    cancelText: string,
    confirmText: string,
    dennyText: string
  ) {
    return Swal.fire({
      title: title,
      text: text,
      showDenyButton: dennyText != '',
      showCancelButton: cancelText != '',
      showConfirmButton: confirmText != '',

      confirmButtonText: confirmText,
      denyButtonText: dennyText,
      cancelButtonText: cancelText,
    });
  }

  htmloptionSwal(
    title: string,
    htmltext: string,
    cancelText: string,
    confirmText: string,
    dennyText: string,
    useInput: SweetAlertInput | null
  ) {
    if (useInput)
      return this.htmloptionSwalAux(
        title,
        htmltext,
        cancelText,
        confirmText,
        dennyText,
        useInput
      );
    else
      return this.htmloptionSwalAuxWithoutInput(
        title,
        htmltext,
        cancelText,
        confirmText,
        dennyText
      );
  }

  private htmloptionSwalAux(
    title: string,
    htmltext: string,
    cancelText: string,
    confirmText: string,
    dennyText: string,
    inputType: SweetAlertInput
  ) {
    return Swal.fire({
      title: title,
      html: htmltext,
      input: inputType,
      showDenyButton: dennyText != '',
      showCancelButton: cancelText != '',
      showConfirmButton: confirmText != '',
      confirmButtonText: confirmText,
      denyButtonText: dennyText,
      cancelButtonText: cancelText,
    });
  }

  private htmloptionSwalAuxWithoutInput(
    title: string,
    htmltext: string,
    cancelText: string,
    confirmText: string,
    dennyText: string
  ) {
    return Swal.fire({
      title: title,
      html: htmltext,
      showDenyButton: dennyText != '',
      showCancelButton: cancelText != '',
      showConfirmButton: confirmText != '',
      confirmButtonText: confirmText,
      denyButtonText: dennyText,
      cancelButtonText: cancelText,
    });
  }
}

import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Driver } from 'src/app/models/models';
import { RestApiServiceService } from 'src/app/Services/rest-api-service.service';
import { SesionService } from 'src/app/Services/sesion-service.service';
import { ActivatedRoute } from '@angular/router';
@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css'],
})
export class ProfileComponent implements OnInit {
  pilots: Driver[] = [];
  top: any[] = [];
  constructor(
    private restApi: RestApiServiceService,
    private router: Router,
    private sesionService: SesionService,
    private route: ActivatedRoute
  ) {}
  user: any;
  myteams: any[] = [];
  ngOnInit(): void {
    this.route.queryParams.subscribe((params) => {
      this.restApi
        .get_request('Players/' + params.userName, null)
        .subscribe((value) => {
          console.log(value);

          this.user = value;
          this.getDrivers();
          this.getTop();
          //this.fillWithDummy();
          this.sortTop();
        });
    });
  }

  getDrivers(): void {
    this.pilots.push;
    this.restApi
      .get_request('PlayerTeams/' + this.user.username, null)
      .subscribe((result: any[]) => {
        this.myteams = result;

        this.myteams.forEach((team) => {
          team.drivers.forEach((driver: any) => {
            driver.country =
              'https://countryflagsapi.com/png/' + driver.country;
          });
        });
      });
  }
  getTop(): void {
    this.restApi
      .get_request('PublicLeague', null)
      .subscribe((result: any[]) => {
        console.log(result);
        this.top = result;
      });
  }

  replaceComponent(component: any): void {
    localStorage.setItem('currentAction', 'replacing');
    localStorage.setItem('selectedDriver', JSON.stringify(component));
    this.router.navigateByUrl('/pages/store');
  }
  createNewTeam(): void {
    this.router.navigateByUrl('/pages/store');
    localStorage.setItem('currentAction', 'creatingTeam');
  }

  sortTop(): void {
    const x = this.top;
    x.sort((value1, value2) => {
      return value2.score - value1.score;
    });
    this.top = x;
  }

  fillWithDummy(): void {
    this.restApi
      .get_requestByUrl(
        'https://random-word-api.herokuapp.com/word?number=10',
        null
      )
      .subscribe((words) => {
        words.forEach((word: string) => {
          this.top.push({
            playerUsername: word,
            teamName: word,
            score: Math.floor(Math.random() * 500),
          });
        });
      });
  }

  openProfile(userName: string): void {
    this.router.navigate(['pages/profile'], {
      queryParams: { userName: userName },
    });
  }
}

import { Component, OnInit } from '@angular/core';
import { CheckerService } from 'src/app/Services/checker.service';
import { CountryService } from 'src/app/Services/country-service.service';
import { RestApiServiceService } from 'src/app/Services/rest-api-service.service';
import { SwalService } from 'src/app/Services/swal-service.service';
import { Md5 } from 'ts-md5/dist/md5';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css'],
})
export class RegisterComponent implements OnInit {
  constructor(
    private countryService: CountryService,
    private swal: SwalService,
    private backend: RestApiServiceService,
    private checker: CheckerService
  ) {}
  rangosEdad: string[] = ['18-25', '25-35', '35-50', '50+'];
  countries: any = ['Costa Rica', 'Panamá'];

  ngOnInit(): void {
    const restCountries = this.countryService.getCountries();
    if (restCountries && restCountries !== [] && restCountries.length > 0)
      this.countries = restCountries;
    console.log(this.countries);
  }

  validateEmail(email: string): boolean {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(email)) {
      return true;
    }
    return false;
  }

  validateFullValues(data: any): boolean {
    const country = data.country != '';
    const ageRange = data.ageRange != '';
    const name = data.name != '';
    const username = data.username != '';
    const email = data.email != '';
    const password = data.password != '';
    return country && ageRange && name && username && email && password;
  }

  writeErrorMessage(
    nameCheck: boolean,
    usernameCheck: boolean,
    passwordIsValid: boolean,
    mailIsValid: boolean,
    allFieldsFilled: boolean
  ) {
    let message =
      'Se detectaron los siguientes errores con el formulario de registro: <br>';
    if (!nameCheck)
      message +=
        ' El nombre no es válido, debe ser un conjunto alfa numerico no vacio<br>';
    if (!usernameCheck)
      message +=
        ' El nombre de usuario no es válido, debe ser un conjunto alfa numerico no vacio<br>';
    if (!passwordIsValid)
      message +=
        ' La contraseña no es  válida, debe ser un conjunto alfa numerico con mínimo una mayúscula, una minúscula y entre 10 y 16 dígitos<br>';
    if (!mailIsValid) message += ' El correo ingresado no es válido<br>';
    if (!allFieldsFilled) message = ' Por favor llene todos los espacios<br>';

    if (!nameCheck || !usernameCheck || !passwordIsValid || !mailIsValid)
      this.swal.showErrorHTML('Datos ingresados Incorrectos', message);
  }

  verifyData(data: any) {
    const nameCheck = this.checker.verifyName(data.name, 5, 30, false);
    const usernameCheck = this.checker.verifyName(data.username);
    const passwordIsValid = this.checker.verifyPassword(data.password);
    const mailIsValid = true || this.checker.validateEmail(data.mail);
    const othersFieldsFilled = this.checker.validateFullValues(data);
    this.writeErrorMessage(
      nameCheck,
      usernameCheck,
      passwordIsValid,
      mailIsValid,
      othersFieldsFilled
    );
    return nameCheck && usernameCheck && passwordIsValid && mailIsValid;
  }

  /**
   * 
     "username": "string",
  "password": "string",
  "name": "string",
  "lastName": "string",
  "email": "string",
  "country": "string",
  "ageRange": "string"
   * 
   * 
    country: "Cambodia"
    email: "asdas"
    name: "ss"
    lastName: "string"
    password: "asd"
    username: "adriantec2019@gmail.com"
   * 
   */
  register(data: any) {
    if (this.verifyData(data)) this.submitRegister(data);
  }

  submitRegister(data: any) {
    data.password = Md5.hashStr(data.password);

    console.log(data);
    this.backend.post_request('Players', data).subscribe(
      (result) => {
        this.swal.showSuccess(
          'Registro exitoso',
          'Se ha enviado el link de verificacion  a tu correo electronico'
        );
      },
      (error) => {
        this.swal.showError(
          'Registro Fallido',
          'El correo electronico o nombre de usuario ya pertenece a un usuario'
        );
      }
    );
  }
}

import { Component, OnInit } from '@angular/core';
import { MatSnackBar } from '@angular/material/snack-bar';
import { NavigationEnd, Router } from '@angular/router';
import { Driver, Scuderia } from 'src/app/models/models';
import { CheckerService } from 'src/app/Services/checker.service';
import { RestApiServiceService } from 'src/app/Services/rest-api-service.service';
import { SesionService } from 'src/app/Services/sesion-service.service';
import { SwalService } from 'src/app/Services/swal-service.service';

@Component({
  selector: 'app-store',
  templateUrl: './store.component.html',
  styleUrls: ['./store.component.css'],
})
export class StoreComponent implements OnInit {
  pilots: Driver[] = [];
  scuderias: Scuderia[] = [];
  currentAction: any = '';
  total: number = 0;
  team1: any = {
    drivers: [],
    scudery: {},
  };

  money: number = 0;
  moneyCounter: any;
  seeingDrivers: boolean = true;

  constructor(
    private restApi: RestApiServiceService,
    private sesionService: SesionService,
    private swalService: SwalService,
    private checker: CheckerService,
    private router: Router,
    private _snackBar: MatSnackBar
  ) {}

  ngOnInit(): void {
    this.currentAction = localStorage.getItem('currentAction');
    this.getDrivers();
    this.getScuderias();

    this.router.events.subscribe((val) => {
      if (val instanceof NavigationEnd)
        this.moneyCounter.innerHTML = this.sesionService.getUser().money;
    });
  }
  
  addPilotToCar(thing: any) {
    if (this.currentAction === 'replacing') {
      this.total = thing.price;
      const hasEnoughMoney = this.sesionService.getUser().money >= this.total;
      const componentBeingReplaced = JSON.parse(
        localStorage.getItem('selectedDriver') as any
      );

      if (hasEnoughMoney)
        this.swalService
          .htmloptionSwal(
            'Compra de un nuevo piloto',
            'Desear reemplazar : ' +
              componentBeingReplaced.name +
              '<br> por: ' +
              thing.name,
            'No',
            'Sí',
            '',
            null
          )
          .then((value) => {
            console.log(value);
            if (value.isConfirmed) {
            }
          });
      else {
        this.swalService.showError(
          'Fondos insuficientes',
          'Actualmente no cuenta con los fondos para realizar esta compra'
        );
      }
      return;
    }

    if (this.team1.drivers.length < 5 && !this.team1.drivers.includes(thing)) {
      this.team1.drivers.push(thing);
      this.total += thing.price;
      this.moneyCounter = document.getElementById('money');
      this.moneyCounter.innerHTML =
        parseInt(this.moneyCounter.innerHTML) - thing.price;

      this._snackBar.open('Piloto Agregado', '', {
        duration: 1000,
      });
    } else
      this.swalService.showError(
        'Acción inválida',
        'No puede agregar mas de cinco pilotos ni agregar pilotos repetidos'
      );
  }

  addCarToCar(thing: any) {
    if (Object.values(this.team1.scudery).length > 0)
      this.total -= this.team1.scudery.price;
    this.team1.scudery = thing;
    this.total += thing.price;
    this._snackBar.open('Escudería cambiada', '', {
      duration: 3000,
    });
  }
  objIsEmpty(value: any) {
    return Object.keys(value).length === 0;
  }

  openDrivers(): void {
    this.seeingDrivers = true;
  }
  openCars(): void {
    this.seeingDrivers = false;
  }

  getDrivers(): void {
    this.pilots.push;
    this.restApi.get_request('Drivers', null).subscribe((result: any[]) => {
      this.pilots = result;
      this.pilots.forEach((driver) => {
        driver.country = 'https://countryflagsapi.com/png/' + driver.country;
      });
      console.log(this.pilots);
    });
  }
  getScuderias(): void {
    this.scuderias.push;
    this.restApi.get_request('Scuderias', null).subscribe((result: any[]) => {
      this.scuderias = result;
    });
  }

  checkPayment(team: any, drivers: any): boolean {
    if (!team) {
      this.swalService.showError(
        'Nombre inválido',
        'Solo puede ingresar un conjunto de valores alfanuméricos no vacío'
      );

      return false;
    }
    const teamName = team;
    const hasEnoughMoney = this.sesionService.getUser().money >= this.total;
    const haveFiveDrivers = this.team1.drivers.length == 5;
    const haveAScudery = Object.values(this.team1.scudery).length > 1;
    const nameNotEmpty = this.checker.verifyName(teamName, 0, 60, true);
    if (!hasEnoughMoney) {
      this.swalService.showError(
        'Fondos insuficientes',
        'Actualmente no cuenta con los fondos para realizar esta compra'
      );
      return false;
    }
    if (!haveFiveDrivers || !haveAScudery) {
      this.swalService.showError(
        'Equipo incompleto',
        'Debe crear un equipo con 5 pilotos y una escudería'
      );
      return false;
    }

    if (!nameNotEmpty) {
      this.swalService.showError(
        'Nombre inválido',
        'Solo puede ingresar un conjunto de valores alfanuméricos no vacío'
      );

      return false;
    }

    return hasEnoughMoney && haveFiveDrivers && haveAScudery;
  }

  pay(): void {
    const teamName = (document.getElementById('teamName') as HTMLInputElement)
      .value;
    const drivers = this.team1.drivers.map((driver: any) => {
      return driver.id;
    });

    if (this.checkPayment(teamName, drivers)) {
      const survey = {
        name: teamName,
        player: this.sesionService.getUser().username,
        scuderia: this.team1.scudery.id,
        drivers: drivers,
      };

      this.submitPay(survey);
    }
  }

  updateUserData() {
    this.restApi
      .get_request('Players/' + this.sesionService.getUser().username, null)
      .subscribe((userData) => {
        this.sesionService.setUser(JSON.stringify(userData));
        this.router.navigateByUrl('pages/myPortal');
      });
  }

  submitPay(data: any) {
    this.restApi.post_request('PlayerTeams', data).subscribe((result) => {
      console.log(result);
      this.swalService.showSuccess(
        'Equipo creado',
        'El equipo fue creado con éxito'
      );

      this.updateUserData();
    });
  }
}
